mapper.select:
SELECT * FROM ( SELECT A.*,ROWNUM num FROM (
select

from BASE_MENU

	
		UP_MENU_ID = #{upMenuId}
	
	
		AND MENU_NAME like '%$menuName%'
	

order by menu_id
) A WHERE ROWNUM<=($currPage$*$pageSize$)
) WHERE num>=
(1+($currPage$-1)*$pageSize$)

select count(t.menu_id) from BASE_MENU t

	
		UP_MENU_ID = #{upMenuId}
	
	
		AND MENU_NAME like '%$menuName%'
