mapper.select:
SELECT * FROM ( SELECT A.*,ROWNUM num FROM (
select

from BASE_MENU
where menu_id!=0

	and UP_MENU_ID = #{upMenuId}


	AND MENU_NAME like '%${keyWord}%'

order by MENU_SORT,MENU_ID
) A WHERE ROWNUM<=(${currPage}*${pageSize})
) WHERE num>=
(1+(${currPage}-1)*${pageSize})

select count(t.menu_id) from BASE_MENU t where menu_id!=0
and UP_MENU_ID = #{upMenuId}
AND MENU_NAME like '%${keyWord}%'
