mapper.select:
SELECT * FROM ( SELECT A.*,ROWNUM num FROM ( SELECT U.USER_ID,U.USER_NAME,U.USER_DEPT_NAME FROM BASE_POST_USER PU LEFT JOIN VIEW_BASE_USER U ON U.USER_ID = PU.USER_ID
AND POST_ID = #{postId}
ORDER BY U.USER_ID ) A WHERE ROWNUM<=(${currPage}*${pageSize}) ) WHERE num>= (1+(${currPage}-1)*${pageSize})

SELECT * FROM ( SELECT A.*,ROWNUM num FROM ( SELECT U.USER_ID,U.USER_NAME,U.USER_DEPT_NAME FROM VIEW_BASE_USER U WHERE USER_ID IN ( SELECT USER_ID FROM BASE_USER MINUS SELECT USER_ID FROM BASE_POST_USER PU
AND POST_ID = #{postId}
) ORDER BY U.USER_ID ) A WHERE ROWNUM<=(${currPage}*${pageSize}) ) WHERE num>= (1+(${currPage}-1)*${pageSize})

SELECT count(1) FROM VIEW_BASE_USER U WHERE USER_ID IN ( SELECT USER_ID FROM BASE_USER MINUS SELECT USER_ID FROM BASE_POST_USER PU
AND POST_ID = #{postId}
)
