mapper.select:
select b.TOOL_DEMAND_ID as TOOL_ID,d.DETAIL_BILL_AMOUNT AS TOOL_ARRIVE_AMOUNT from GQJ_MATERIAL_BILL_DETAIL d left join bpbj_base_tool b on b.base_tool_id = d.base_tool_id where d.bill_id in ( select t.bill_id from GQJ_MATERIAL_BILL t
AND t.PLAN_ID = #{planId}
)

select * from VIEW_GQJ_DEMAND_PLAN_DETAIL

	
		AND PLAN_ID = #{planId}
	

order by DETAIL_ID

SELECT * FROM ( SELECT A.*,ROWNUM num FROM (
select

from GQJ_DEMAND_PLAN_DETAIL

	
		AND PLAN_ID = #{planId}
	

order by PLAN_ID
) A WHERE ROWNUM<=(${currPage}*${pageSize})
) WHERE num>=
(1+(${currPage}-1)*${pageSize})
