mapper.select:
SELECT * FROM ( SELECT A.*,ROWNUM num FROM ( select
from VIEW_BPBJ_PLUGIN
AND PLUGIN_CODE like '%${keyWord}%'
and STORE_ID = #{storeId,jdbcType=DECIMAL}
and POS_ID = #{posId,jdbcType=DECIMAL}
AND BASE_PLUGIN_MODEL like '%${basePlugInModel}%'
AND BASE_PLUGIN_SPEC like '%${basePlugInSpec}%'
and MAN_ID = #{manufacturerId,jdbcType=DECIMAL}
and PLUGIN_DEPT_ID = #{plugInDeptId,jdbcType=DECIMAL}
and PLUGIN_STATUS = #{plugInStatus,jdbcType=DECIMAL}
and to_date(#{overTestDays},'yyyy-mm-dd') > PLUGIN_NEXT_TEST_DATE
and to_date(#{overRejectDays},'yyyy-mm-dd') > PLUGIN_REJECT_DATE
order by PLUGIN_ID ) A WHERE ROWNUM<=(${currPage}*${pageSize}) ) WHERE num>= (1+(${currPage}-1)*${pageSize})

select count(t.PLUGIN_ID) from VIEW_BPBJ_PLUGIN t

	
		AND PLUGIN_CODE like '%${keyWord}%'
	
	
		and STORE_ID =
		#{storeId,jdbcType=DECIMAL}
	
	
		and POS_ID =
		#{posId,jdbcType=DECIMAL}
	
	
		and BASE_PLUGIN_TYPE_ID =
		#{basePlugInTypeId,jdbcType=DECIMAL}
	
	
		AND BASE_PLUGIN_MODEL like '%${basePlugInModel}%'
	
	
		AND BASE_PLUGIN_SPEC like '%${basePlugInSpec}%'
	
	
		and BASE_PLUGIN_MANUFACTURER_ID =
		#{manufacturerId,jdbcType=DECIMAL}
	
	
		and PLUGIN_DEPT_ID =
		#{plugInDeptId,jdbcType=DECIMAL}
	
	
		and PLUGIN_STATUS =
		#{plugInStatus,jdbcType=DECIMAL}
	
	
		and to_date(#{overTestDays},'yyyy-mm-dd') >
		PLUGIN_NEXT_TEST_DATE
	
	
		and to_date(#{overRejectDays},'yyyy-mm-dd') >
		PLUGIN_REJECT_DATE

mapper.delete:
delete from
BPBJ_PLUGIN

	
		and PLUGIN_ID in (${ids})
	
	
		AND PLUGIN_ID=#{plugInId}
	
	
		AND BATCH_ID=#{batchId}
