mapper.select:
SELECT * FROM ( SELECT A.*,ROWNUM num FROM (
select

from VIEW_GQJ_MATERIAL

	
		AND MAT_NAME like '%${keyWord}%'
	
	
		AND TYPE_ID=#{typeId}
	

order by MAT_ID
) A WHERE ROWNUM<=(${currPage}*${pageSize})
) WHERE num>=
(1+(${currPage}-1)*${pageSize})

select count(t.MAT_ID) from VIEW_GQJ_MATERIAL t

	
		AND MAT_NAME like '%${keyWord}%'
	
	
		AND TYPE_ID=#{typeId}

mapper.delete:
delete from
GQJ_MATERIAL
where MAT_ID in #{ids}
