mapper.select:
SELECT * FROM ( SELECT A.*,ROWNUM num FROM (
select

from VIEW_GQJ_POSITION

	
		and POS_DEPT_ID = #{posDeptId}
	
	
		and STORE_ID = #{storeId}
	
	
		AND POS_NAME like '%${keyWord}%'
	

order by POS_ID
) A WHERE ROWNUM<=(${currPage}*${pageSize})
) WHERE num>=
(1+(${currPage}-1)*${pageSize})

select count(t.POS_ID) from VIEW_GQJ_POSITION t

	
		and POS_DEPT_ID = #{posDeptId}
	
	
		and STORE_ID = #{storeId}
	
	
		AND POS_NAME like '%${keyWord}%'
