mapper.select:
SELECT * FROM ( SELECT A.*,ROWNUM num FROM (
select

from VIEW_GQJ_TOOL_FOR_PLAN

	
		AND TOOL_NAME like '%${keyWord}%'
	
	
		AND TYPE_ID=#{typeId}
	

order by TOOL_ID DESC
) A WHERE ROWNUM<=(${currPage}*${pageSize})
) WHERE num>=
(1+(${currPage}-1)*${pageSize})

select count(t.TOOL_ID) from VIEW_GQJ_TOOL_FOR_PLAN t

	
		AND TOOL_NAME like '%${keyWord}%'
	
	
		AND TYPE_ID=#{typeId}

mapper.delete:
delete from
GQJ_TOOL_FOR_PLAN
where TOOL_ID in #{ids}
